function getCategories(n){$.ajax({url:"/CategoriesShort",type:"GET",contentType:"application/json; charset=utf-8",datatype:JSON,success:function(t){$(t).each(function(t,i){var r=$("<option><\/option>").val(i.id).html(i.name);$(n).append(r)})},error:function(){}})}function addCategory(n){$.ajax({url:"/Categories",type:"POST",contentType:"application/json; charset=utf-8",data:JSON.stringify({Name:$("#newCategoryName").val()}),datatype:JSON,success:function(t){if(t.isValid==!0){var i=$("<option><\/option>").val(t.model.id).html(t.model.name);$(n).append(i);updateAnimation(n)}else raiseError(t.error)},error:function(){}})}function updateAnimation(n){closeModal();raiseSuccess("The category was added successfully.");updateSelection(n);$(n).fadeOut(300).fadeIn(300)}function updateSelection(n){n=n.substring(1);var t=document.getElementById(n);t.selectedIndex=t.length-1}function raiseSuccess(n){$("#success_placeholder").html('<div class="alert alert-dismissible alert-success"> <button type="button" class="close" data-dismiss="alert">&times;<\/button><small>'+n+"<\/small><\/div>")}function raiseError(n){$("#alert_placeholder").html('<div id="catErrors" class="alert alert-danger"><small>'+n+"<\/small><\/div>")}function closeModal(){try{$("#catErrors").alert("close");$("#addCategory").collapse("hide")}catch(n){}}