function getCategories(){$.ajax({url:"Categories/ReturnJSONCategories",type:"GET",contentType:"application/json; charset=utf-8",datatype:JSON,success:function(n){$(n).each(function(n,t){var i=$("<option><\/option>").val(t.id).html(t.name);$("#catList").append(i)})},error:function(){}})}function addCategory(){$.ajax({url:"Categories",type:"POST",contentType:"application/json; charset=utf-8",data:JSON.stringify({Name:$("#newCategoryName").val()}),datatype:JSON,success:function(n){if(n.isValid==!0){var t=$("<option><\/option>").val(n.model.id).html(n.model.name);$("#catList").append(t);updateAnimation(t)}else raiseError(n.error)},error:function(){}})}function updateAnimation(){closeModal();raiseSuccess("The category was added successfully.");updateSelection();$("#catList").fadeOut(300).fadeIn(300)}function updateSelection(){var n=document.getElementById("catList");n.selectedIndex=n.length-1}function raiseSuccess(n){$("#success_placeholder").html('<div class="alert alert-dismissible alert-success"> <button type="button" class="close" data-dismiss="alert">&times;<\/button><small>'+n+"<\/small><\/div>")}function raiseError(n){$("#alert_placeholder").html('<div id="catErrors" class="alert alert-danger"><small>'+n+"<\/small><\/div>")}function closeModal(){try{$("#catErrors").alert("close")}catch(n){}$("#addCategory").collapse("hide")}$(document).ready(getCategories);$("#newCategoryName").on("focusout",function(){addCategory()});