function login(){$.ajax({url:"/Login",type:"POST",contentType:"application/json; charset=utf-8",data:JSON.stringify({Email:$("#email").val(),Password:$("#password").val(),RememberMe:$("#remember-me").is(":checked")}),datatype:JSON,success:function(n){if(n.isValid==!0)try{$("#modal-trigger").click();$("#catErrors").alert("close");location.reload()}catch(t){}else raiseError(n.error)},error:function(){}})}function raiseError(n){$("#login_error").html('<div id="catErrors" class="text-danger pb-2"><small>'+n+"<\/small><\/div>")}function onOpenLoginModal(){$("#login-trigger").on("click",function(){try{$("#login-modal").modal()}catch(n){}})}function onAddProductOpenModal(){$("#modal-trigger").on("click",function(){try{var n=document.getElementById("login-modal");n!=null?$("#login-modal").modal():$("#addProductModal").modal()}catch(t){}})}function onEnterPressLogin(){$("#login-form").on("keypress",function(n){var t=n.keyCode||n.which;t==13&&$("#submit-login").click()})}$(document).ready(function(){onOpenLoginModal();onAddProductOpenModal();onEnterPressLogin()});