function getCities(){$(["Afula","Azor","Ashdod","Arad","Ashkelon","Bat-Yam","BeerSheva","Bazra","Ashdod","Holon","Eilat","Krayot"]).each(function(n,t){var i=$("<option><\/option>").val(t).html(t);$("#citiesList").append(i)})}function searchProducts(){$.ajax({url:"/Products/Search",type:"POST",contentType:"application/json; charset=utf-8",data:JSON.stringify({Name:$("#search-name").val(),Category:$("#search-category").val(),MinPrice:$("#min-price").val(),MaxPrice:$("#max-price").val(),Location:{City:$("#search-city").val(),StreetAddress:$("#search-address").val()}}),datatype:JSON,success:function(n){try{$("#search-menu-trigger").click()}catch(t){console.log("couldn't close menu.")}$("#productBody").html(n)}})}function getProducts(){$.ajax({url:"/Products/GetJsonProducts",type:"GET",contentType:"application/json; charset=utf-8",success:function(n){$("#productBody").html(n);$("#productBody").fadeIn("slow")}})}function updateDatepickers(){try{document.getElementById("availableFrom").valueAsDate=new Date;document.getElementById("availableUntil").valueAsDate=new Date}catch(n){}}$(document).ready(updateDatepickers);