function showCurrencies(){$.get("http://openexchangerates.org/api/latest.json?app_id=d04a1cbf0f14493c973fc7087c0629fb").then(function(n){let t=$("#price_input").val();if(t!==""){let u=n.rates.ILS,f=parseFloat(t)/parseFloat(u),i=f.toString(),r=i.indexOf(".");r>0&&(i=i.substring(0,r+3));console.log(i);$("#usd_value").html("("+i+" $)")}})}